mapper.update:
UPDATE
tbItemBuyList
SET 
  storeBuyCode = #{storeBuyCode}
, storeBuyItemCode = #{storeBuyItemCode}
, userBuyId	= #{userBuyId}
, itemBuyPrice = #{itemBuyPrice}
, itemBuyAccount = #{itemBuyAccount}
, itemBuyPhone = #{itemBuyPhone}
WHERE
	itemBuyCode = #{itemBuyCode};

mapper.insert:

INSERT INTO tbItemBuyList
	(
	 storeBuyCode
	, storeBuyItemCode
	, userBuyId
	, itemBuyPrice
	, itemBuyAccount
	, itemBuyPhone
	, itemBuyRegDate)
VALUES 
	(
	 #{storeBuyCode}
	, #{storeBuyItemCode}
	, #{userBuyId}
	, #{itemBuyPrice}
	, #{itemBuyAccount}
	, #{itemBuyPhone}
	, #{itemBuyRegDate}	)

