mapper.select:

FROM 
	tbReview
WHERE
	reviewStoreId = #{sessionId}
ORDER BY reviewCode DESC
LIMIT #{startRow}, #{rowPerPage};

mapper.update:
UPDATE tbReview
SET
	reviewDisplay = #{blindValue}
WHERE
	reviewCode = #{reviewCode};

