mapper.update:
UPDATE tbRsv
SET
	rsvState=#{rsvState}
WHERE 
 rsvCode = #{rsvCode}

mapper.select:
SELECT rsvDetailSpace ,rsvDetailItem
,rsvStartDateTime ,rsvEndDateTime
FROM tbRsv AS r INNER JOIN tbRsvRelation AS a ON r.rsvCode = a.rsvRelationRsvCode INNER JOIN tbRsvDetail AS d ON a.rsvRelationRsvDetailCode = d.rsvDetailCode WHERE r.rsvCode= #{rsvCode};

SELECT rsvCode , rsvUserId , rsvStoreCode , rsvUserName , rsvUserPhone , rsvUserEmail , rsvUserRequest , rsvTotalPrice , rsvStartDateTime , rsvEndDateTime , rsvRegDate , rsvState , storeId
FROM tbRsv AS r INNER join tbStore AS s on rsvStoreCode = storeCode
WHERE rsvCode = #{rsvCode};

mapper.insert:
INSERT INTO tbPayment
(

 paymentPrice
, paymentRsvCode
, paymentUsePoint
, paymentUseMoney
, paymentAddPoint
, paymentStoreCode
, paymentIP
, paymentRegDate
, paymentStoreId
)
VALUES (

#{paymentPrice}
, #{paymentRsvCode}
, #{paymentUsePoint}
, #{paymentUseMoney}
, #{paymentAddPoint}
, #{paymentStoreCode}
, #{paymentIP}
, NOW()
, #{paymentStoreId}
)

INSERT INTO tbPointList
	(
	 pointID
	, pointAddList
	, pointSellList
	, pointList
	, pointModyfiDate
	, pointPaymentCode)
VALUES 
	( 
	
	 #{pointID}
	, #{pointAddList}
